import React, { useState } from 'react';

interface NoteFormProps {
  onSubmit: (title: string, content: string) => void;
}

const NoteForm: React.FC<NoteFormProps> = ({ onSubmit }) => {
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onSubmit(title, content);
    setTitle('');
    setContent('');
  };

  return (
    <div className="max-w-md mx-auto">
      <div className="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4">
        <h5 className="mb-4 text-xl font-semibold">Create a New Note</h5>
        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <input
              type="text"
              className="block appearance-none w-full border border-gray-200 rounded py-2 px-3 leading-tight focus:outline-none focus:border-blue-500"
              placeholder="Title"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              required
            />
          </div>
          <div className="mb-6">
            <textarea
              className="block appearance-none w-full border border-gray-200 rounded py-2 px-3 leading-tight focus:outline-none focus:border-blue-500"
              placeholder="Content"
              value={content}
              onChange={(e) => setContent(e.target.value)}
              required
            ></textarea>
          </div>
          <button type="submit" className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline">
            Add Note
          </button>
        </form>
      </div>
    </div>
  );
};

export default NoteForm;
